import "./contact.css";
import Phone from "../../img/phone.png";
import Email from "../../img/email.png";
import Address from "../../img/address.png";
import { useContext, useRef, useState } from "react";
import emailjs from "emailjs-com";
import { ThemeContext } from "../../context";

const Contact = () => {
  const formRef = useRef();
  const [done, setDone] = useState(false)
  const theme = useContext(ThemeContext);
  const darkMode = theme.state.darkMode;

  const handleSubmit = (e) => {
    e.preventDefault();
    emailjs
      .sendForm(
        "service_wz8j0ju",
        "template_osd4tff",
        formRef.current,
        "user_X3alrZw7I6d5ev4LcIUbb"
      )
      .then(
        (result) => {
          console.log(result.text);
          setDone(true)
        },
        (error) => {
          console.log(error.text);
        }
      );
  };

  return (
    <div className="c">
      <div className="c-bg"></div>
      <div className="c-wrapper">
        <div className="c-left">
          <h1 className="c-title">Let's discuss your project</h1>
          <div className="c-info">
            <div className="c-info-item">
              <img src={Phone} alt="" className="c-icon" />
              +47 947 19 113  
            </div>
            <div className="c-info-item">
              <img className="c-icon" src={Email} alt="" />
              convento_nathan@yahoo.com / nathan1sts5@gmail.com
            </div>
            <div className="c-info-item">
              <img className="c-icon" src={Address} alt="" />
              Ørnahaugen 46, 5144 Fyllingsdalen Norway
            </div>
             {/* <p className="c-info-item">You can also contact me through our social media!</p>

              <a href=".">
                <i className="fab fa-facebook-f" style={{backgroundColor: darkMode && "#333"}}></i>
              </a>
              <a href=".">
                <i className="fab fa-twitter" style={{backgroundColor: darkMode && "#333"}}></i>
              </a>
              <a href=".">
                <i className="fab fa-instagram" style={{backgroundColor: darkMode && "#333"}}></i>
              </a> */}

          </div>
        </div>
        <div className="c-right">
          <p className="c-desc">
            <b>What’s your story?</b> Get in touch. Always available for
            freelancing if the right project comes along. me.
          </p>
          <form ref={formRef} onSubmit={handleSubmit}>
            <input style={{backgroundColor: darkMode && "#333"}} type="text" placeholder="Name" name="user_name" />
            <input style={{backgroundColor: darkMode && "#333"}} type="text" placeholder="Subject" name="user_subject" />
            <input style={{backgroundColor: darkMode && "#333"}} type="text" placeholder="Email" name="user_email" />
            <textarea style={{backgroundColor: darkMode && "#333"}} rows="5" placeholder="Message" name="message" />
            <button>Submit</button>
            {done && "-->  Thank you i will Reply after 8-12 hours Have a Nice Day" }
          </form>
        </div>
      </div>
    </div>
  );
};

export default Contact;
